  @State private var coins: [Coin] = []

  func addCoin(name: String, image: String, value: Int) {
    coins.append(Coin(name: name, image: image, value: value))
  }

  var body: some View {
    VStack {
      ForEach(coins) { coin in
        VStack {
          Image(coin.image)
          Text("Value: \(coin.value)")
        }
      }
    }
  }
}

