import UIKit
import CoreLocation
import CoreBluetooth

class NewsUpdateApp: UIApplication {
    let locationManager = CLLocationManager()
    let newsAPI = NewsAPI()
    let userDatabase = UserDatabase()

    override func applicationDidFinishLaunching(_ application: UIApplication) {
        super.applicationDidFinishLaunching(application)
        locationManager.requestAlwaysAuthorization()
        locationManager.startUpdatingLocation()
    }

    override func applicationDidBecomeActive(_ application: UIApplication) {
        super.applicationDidBecomeActive(application)
        fetchNewsUpdates()
    }

    func fetchNewsUpdates() {
        let userLocation = locationManager.location
        let userDemographics = userDatabase.getUserDemographics(userLocation)

        let newsUpdates = newsAPI.getNewsUpdates(userDemographics)
        for newsUpdate in newsUpdates {
            print("News Update: \(newsUpdate.title) - \(newsUpdate.description)")
        }
    }
}

