import UIKit
import StoreKit

class ViewController: UIViewController, SKPaymentTransactionObserver, SKProductsRequestDelegate {

    let productIds = ["com.example.uselesscoins.100coins", "com.example.uselesscoins.500coins", "com.example.uselesscoins.1000coins"]
    var coins: Int = 0

    override func viewDidLoad() {
        super.viewDidLoad()
        SKPaymentQueue.default().add(self)
    }

    @IBAction func buyCoins(_ sender: UIButton) {
        let product = SKProduct(productIds[sender.tag - 100])
        SKPaymentQueue.default().add(SKPayment(product: product))
    }

    func paymentQueue(_ queue: SKPaymentQueue, updatedTransactions transactions: [SKPaymentTransaction]) {
        for transaction in transactions {
            switch transaction.transactionState {
            case.purchased:
                coins += Int(transaction.payment.product.price)
                SKPaymentQueue.default().finishTransaction(transaction)
            case.failed:
                print("Transaction failed")
                SKPaymentQueue.default().finishTransaction(transaction)
            default:
                break
            }
        }
    }

    func paymentQueueRestoreCompletedTransactions(_ queue: SKPaymentQueue) {
        for transaction in queue.transactions {
            switch transaction.transactionState {
            case.restored:
                coins += Int(transaction.payment.product.price)
            default:
                break
            }
        }
    }

    func productsRequest(_ request: SKProductsRequest, didReceive response: SKProductsResponse) {
        for product in response.products {
            print("Product: \(product.productID), Price: \(product.price)")
        }
    }

    func paymentQueue(_ queue: SKPaymentQueue, restoreCompletedTransactionsCompleted restored: Bool) {
        print("Restore completed")
    }
}
```

