import SwiftUI
import Combine

class GameItem: ObservableObject, Comparable {
  @Published
  var items: [String]
  var cost: Int

  func purchase(with cost: Int) -> Void {
    self.cost = cost
  }

  func sell(to recipient: Player) -> Void {
    recipient.inventory.append(self)
  }
}

class Player: ObservableObject {
  @Published
  var inventory: [GameItem]

  func buy(item: GameItem) -> Void {
    inventory.append(item)
  }

  func useItem(item: GameItem) -> Void {
    inventory.remove(item)
  }
}

class GameMaster: ObservableObject {
  @Published
  var players: [Player]

  func start() -> Void {
    for _ in players.forEach { $0 in print("Player \(players.count) is playing!") }
  }
}

let gameMaster = GameMaster()
gameMaster.start()
```

