import SwiftUI
import Combine

@main
struct LocalNews {
  @Published
  func localizedNews(topic: String, audience: String) -> EventLoop<[String, String]> {
    let url = "news/" + topic + "/local"
    let content = try! LocalNews().loadJSON(url)
    let title = content["title"]
    let summary = content["summary"]
    let sentiment = content["sentiment"]
    let date = content["date"]

    VStack {
      Text("**\(title)")
      Text("By: \(author)")
      Text("Date: \(date)")
      Text("-------------------------------------------------------------------")
      Text("Summary:")
      Text(summary)
      Text("Sentiment:")
      Text(sentiment)
      Text("---------------------------------------------------")
    }

    if sentiment == "positive" {
      Text("We think this proposal is a good idea!")
    } else if sentiment == "negative" {
      Text("We think this proposal is a bad idea.")
    } else {
      Text("We're not sure about this proposal.")
    }

    Text("---------------------------------------------------")
    Text("Please share your thoughts on this proposal in the comments below:")
  }
}

LocalNews().localizedNews(topic: "Tax Reform Proposal", audience: "Young Adults")()
```

