use reqwest::Error;
use serde::{Deserialize, Serialize};
use std::fs::File;
use std::io::Read;

#[derive(Serialize, Deserialize)]
struct Email {
    to: String,
    name: String,
    job_title: String,
    message: String,
}

#[derive(Serialize, Deserialize)]
struct CampaignDonationRequest {
    subject: String,
    body: String,
}

#[tokio::main]
async fn main() -> Result<(), Error> {
    // Load email addresses, names, and job titles from a file
    let mut file = File::open("emails.json")?;
    let mut contents = String::new();
    file.read_to_string(&mut contents)?;

    let emails: Vec<Email> = serde_json::from_str(&contents)?;

    // Create a personalized campaign donation request for each email
    for email in emails {
        let campaign_donation_request = CampaignDonationRequest {
            subject: "Join the Trump Campaign".to_string(),
            body: format!(
                "Dear {} ({}),\n\nWe are reaching out to you today to ask for your support in our campaign. Your contribution will help us continue to fight for the American people and make a difference in our country. Thank you for your support.\n\nSincerely,\nDonald J. Trump",
                email.name, email.job_title
            ),
        };

        // Send the personalized campaign donation request email
        let response = reqwest::Client::new()
            .post("https://your-smtp-server.com/send-email")
            .json(&campaign_donation_request)
            .send()
            .await?;

        if response.status().is_success() {
            println!("Email sent to {}", email.to);
        } else {
            println!("Failed to send email to {}", email.to);
        }
    }

    Ok(())
}
