candidate_stance = {
    "family_unification": "We believe in strong family values and want to ensure that families can stay together.",
    "economic_benefits": "Immigrants contribute to our economy and help fill critical labor shortages.",
    "security_measures": "We also prioritize security and will work to ensure that our borders are safe and secure.",
    "path_to_citizenship": "We support a path to citizenship for those who are here illegally, as long as they meet certain requirements."
}

# Greeting and introduction
def greet():
    print("Hello! I'm your friendly chatbot. Let's chat about immigration and how it affects our community.")
    print("I'm here to share some insights from our candidate on this important issue.")
    print("Feel free to ask me questions or share your thoughts!\n")

# Respond to user input
