    user_input = user_input.lower()

    if "family" in user_input:
        return candidate_stance["family_unification"]
    elif "economy" in user_input or "jobs" in user_input:
        return candidate_stance["economic_benefits"]
    elif "security" in user_input:
        return candidate_stance["security_measures"]
    elif "citizenship" in user_input:
