  @Published var coins: Int = 0

  private let store = Purchases.Store.shared()

  func buyCoins(amount: Int) {
    store.purchase(product: "com.example.myapp.coins", quantity: amount) { (result, error) in
      if let result = result, result.success {
        coins += result.quantity
      } else if let error = error {
        print("Error purchasing coins: \(error)")
      }
    }
  }
}

struct ContentView: View {
  @ObservedObject private var coinManager = CoinManager()

  var body: some View {
    VStack {
      Text("Coins: \(coinManager.coins)")

      Button("Buy Coins") {
        coinManager.buyCoins(amount: 100)
      }

      if coinManager.coins > 0 {
        Button("Spend Coins") {
          // Implement logic to spend coins
        }
      }
    }
  }
}
```

