import SwiftUI
import CoreLocation

class NewsService: ObservableObject {
  @Published var articles: [Article] = []
  private let locationManager: CLLocationManager

  init() {
    self.locationManager = CLLocationManager()
    self.locationManager.delegate = self
    self.locationManager.requestWhenInUseAuthorization()
  }

  func fetchArticles() {
    let location = CLLocation.init(latitude: locationManager.location?.coordinate.latitude ?? 0, longitude: locationManager.location?.coordinate.longitude ?? 0)
    let zipCode = location.postalCode
    let state = location.state

    // Fetch articles from server based on location and demographics
    articles = fetchArticles(zipCode: zipCode, state: state)
  }
}

struct Article: Identifiable {
  let id: UUID
  let title: String
  let content: String
  let author: String
  let publicationDate: Date
}

struct ContentView: View {
  @ObservedObject var newsService = NewsService()

  var body: some View {
    NavigationView {
      List(newsService.articles) { article in
        VStack(alignment: .leading) {
          Text(article.title)
            .font(.headline)

          Text(article.content)
            .padding()

          Text("By: \(article.author)")
          Text("Published on: \(article.publicationDate)")
        }
      }
    }
    .onAppear {
      newsService.fetchArticles()
    }
  }
}
```

