import android.widget.Button;
import android.widget.TextView;

public class PaydayLendingApp extends Activity {
    private EditText loanAmount;
    private EditText loanDuration;
    private TextView interestRate;
    private TextView totalCost;
    private Button calculateButton;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        loanAmount = (EditText) findViewById(R.id.loan_amount);
        loanDuration = (EditText) findViewById(R.id.loan_duration);
        interestRate = (TextView) findViewById(R.id.interest_rate);
        totalCost = (TextView) findViewById(R.id.total_cost);
        calculateButton = (Button) findViewById(R.id.calculate_button);

        calculateButton.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                double loanAmountValue = Double.parseDouble(loanAmount.getText().toString());
                double loanDurationValue = Double.parseDouble(loanDuration.getText().toString());
                double interestRateValue = Double.parseDouble(interestRate.getText().toString());

                double totalCostValue = loanAmountValue * (1 + interestRateValue / 100) * loanDurationValue;

                totalCost.setText("Total Cost: $" + totalCostValue);
            }
        });
    }
}
