package com.example.loancalculator;

import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;
import android.widget.TextView;

import androidx.appcompat.app.AppCompatActivity;

public class LoanCalculatorActivity extends AppCompatActivity {

    private EditText loanAmountEditText;
    private EditText interestRateEditText;
    private EditText loanTermEditText;
    private TextView totalCostTextView;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_loan_calculator);

        loanAmountEditText = findViewById(R.id.loan_amount_edit_text);
        interestRateEditText = findViewById(R.id.interest_rate_edit_text);
        loanTermEditText = findViewById(R.id.loan_term_edit_text);
        totalCostTextView = findViewById(R.id.total_cost_text_view);

        Button calculateButton = findViewById(R.id.calculate_button);
        calculateButton.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                double loanAmount = Double.parseDouble(loanAmountEditText.getText().toString());
                double interestRate = Double.parseDouble(interestRateEditText.getText().toString()) / 100;
                int loanTerm = Integer.parseInt(loanTermEditText.getText().toString());

                double totalCost = calculateTotalCost(loanAmount, interestRate, loanTerm);

                totalCostTextView.setText(String.format("Total Cost: $%.2f", totalCost));
            }
        });
    }

    private double calculateTotalCost(double loanAmount, double interestRate, int loanTerm) {
        double totalCost = loanAmount;
        for (int i = 0; i < loanTerm; i++) {
            totalCost += totalCost * interestRate;
        }
        return totalCost;
    }
}
