 private int term;

 public PaydayLender(double principal, int term) {
 this.principal = principal;
 this.term = term;
 }

 public double calculateInterest() {
 // Interest rate for the first $500
 double first500Interest = 0.15;

 // Interest rate for the remaining amount
 double remainingInterest = 0.20;

 // Calculate the total amount borrowed
 double totalBorrowed = principal + (principal * first500Interest) +
 (principal - 500) * remainingInterest;

 // Calculate the total interest
 double totalInterest = totalBorrowed - principal;

 // Calculate the total payment
 double totalPayment = totalInterest + principal;

 return totalPayment;
 }

 public static void main(String[] args) {
 PaydayLender lender = new PaydayLender(300.0, 14);
 double payment = lender.calculateInterest();
 System.out.println("Total payment: $" + payment);
 }
}
```

