        self.id = None

class Game(db.Model):
    id = db.Column(db.Integer, primary_key=True)
    name = db.Column(db.String)
    description = db.Column(db.Text)

    def __init__(self):
        super(Game, self).__init__()
        self.id = None

# Define Routes
