    var body: some View {
        if isExpanded {
            Text(self.title)
            Text(self.summary)
        }
    }
}

struct NewsViewModel: ObservableObject {

    @Published var news: [News]

    @Published var localizer: Localizer<News>

    func update(with context: Context) {
        localizer.localize(news)
    }
}

