from http.server import BaseHTTPServer
from http.client import HTTPClient
from http.cookies import Cookie
from http.request import Request
from http.response import Response

class Casino:
    def __init__(self):
        self.games = {}
        self.users = {}

    def start(self):
        self.httpd = HTTPServer((('0.0.0.0', 8000))
        print("Server started on port 8000")

    def do_GET(self, request):
        if request.path == '/':
            return self.show_homepage(request)
        elif request.path.startswith('/game/'):
            return self.show_game(request)
        else:
            return self.show_404(request)

    def do_POST(self, request):
        if request.path == '/bet':
            return self.place_bet(request)
        else:
            return self.show_404(request)

    def show_homepage(self, request):
        return Response(200, "Welcome to the Casino!")

    def show_game(self, request):
        game_id = int(request.path.split('/')[-1])
        if game_id in self.games:
            return Response(200, self.games[game_id].description)
        else:
            return Response(404, "Game not found")

    def show_404(self, request):
        return Response(404, "Page not found")

    def place_bet(self, request):
        user = self.users.get(request.headers.get('cookie'))
        if user:
            game_id = int(request.json()['game_id'])
            if game_id in self.games:
                self.games[game_id].bet(user, int(request.json()['amount']))
                return Response(200, "Bet placed")
            else:
                return Response(404, "Game not found")
        else:
            return Response(401, "Unauthorized")

    def start(self):
        self.start()
        self.httpd.serve_forever()

if __name__ == '__main__':
    casino = Casino()
    casino.start()
