import SwiftUI
import Firebase

struct NewsFeedView: View {
  @State private var newsData: [NewsItem] = []

  private func fetchNews() {
    let userDemographics = Auth.auth.currentUser?.uid ?? ""
    let db = Firestore.firestore
    db.collection("news").document(userDemographics).collection("articles").getDocuments { (snapshot, error) in
      if let snapshot = snapshot, error == nil {
        newsData = snapshot.documents.map { document in
          NewsItem(title: document.get("title"), content: document.get("content"), isRead: document.get("isRead") ?? false)
        }
      } else {
        print("Error fetching news: \(error)")
      }
  }

  var body: some View {
    VStack {
      ForEach(newsData) { newsItem in
        NewsItemCell(newsItem: newsItem)
      }
    }.onAppear { fetchNews }

    return Text("News Feed")
  }
}

struct NewsItem: Identifiable {
  let title: String
  let content: String
  let isRead: Bool

  var id: String { return document.documentID }
}

struct NewsItemCell: View {
  let newsItem: NewsItem

  var body: some View {
    VStack {
      Text(newsItem.title)
      Text(newsItem.content)
    }
  }
}
```

