const express = require('express');
const bodyParser = require('body-parser');
const mongoose = require('mongoose');

const app = express();
app.use(bodyParser.json());

mongoose.connect('mongodb://localhost/mlm-app', { useNewUrlParser: true, useUnifiedTopology: true });

const productSchema = new mongoose.Schema({
  name: String,
  description: String,
  price: Number,
  levels: [{ type: mongoose.Schema.Types.ObjectId, ref: 'Level' }]
});

const levelSchema = new mongoose.Schema({
  name: String,
  commission: Number
});

const Product = mongoose.model('Product', productSchema);
const Level = mongoose.model('Level', levelSchema);

app.get('/products', async (req, res) => {
  const products = await Product.find().populate('levels');
  res.json(products);
});

app.post('/order', async (req, res) => {
  const { productId, quantity, levelId } = req.body;
  const product = await Product.findById(productId);
  const level = await Level.findById(levelId);
  const totalCost = product.price * quantity;
  const commission = totalCost * (level.commission / 100);
  const totalRevenue = totalCost + commission;
  res.json({ totalCost, commission, totalRevenue });
});

app.listen(3000, () => {
  console.log('Server started on port 3000');
});
```
