Here's a sample code for an iOS app that provides localized news updates tailored to users' demographics:

```swift
import UIKit
import Alamofire
import SwiftyJSON

class NewsViewController: UIViewController {

    @IBOutlet weak var tableView: UITableView!

    var newsData: [News] = []

    override func viewDidLoad() {
        super.viewDidLoad()

        tableView.dataSource = self
        tableView.delegate = self

        fetchNewsData()
    }

    func fetchNewsData() {
        Alamofire.request("https://api.example.com/news", method: .get, parameters: ["demographic": "your_demographic"], encoding: JSONEncoding.default).responseJSON { (response) in
            switch response.result {
            case .success(let value):
                let json = JSON(value)
                self.newsData = json.arrayValue.map { News(json: $0) }
                self.tableView.reloadData()
            case .failure(let error):
                print(error)
            }
        }
    }
}

class News: NSObject {

    var title: String
    var description: String

    init(json: JSON) {
        self.title = json["title"].stringValue
        self.description = json["description"].stringValue
    }
}

extension NewsViewController: UITableViewDataSource {
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        return newsData.count
    }

    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        let cell = tableView.dequeueReusableCell(withIdentifier: "NewsCell", for: indexPath) as! NewsCell
        cell.titleLabel.text = newsData[indexPath.row].title
        cell.descriptionLabel.text = newsData[indexPath.row].description
        return cell
    }
}
