import SwiftUI
import StoreKit

struct CoinGameView: View {
  @State private var coins: Int = 100
  @State private var isPurchased = false

  private let productID: String = "com.example.coinGame.iap.coins"

  var body: some View {
    VStack {
      Text("You have \(coins) coins")
      Button("Spin") {
        spin()
      }
      .disabled(coins == 0)

      if isPurchased {
        Text("You have purchased coins!")
      }
    }
    .onAppear {
      checkPurchased()
    }
  }

  private func spin() {
    let random = Int.random(in: 1...10)
    coins -= random
  }

  private func checkPurchased() {
    let storeKit = StoreKit.sharedInstance()
    storeKit.products(with: Set(["\(productID)"]),
      nil,
      completionHandler: { (products, error) in
        if let products = products, let firstProduct = products.first {
          isPurchased = firstProduct.availableForPurchase
          coins = Int(firstProduct.price.product.integerValue)
        } else {
          print("Error: \(error)")
        }
      })
  }
}

struct CoinGameView_Previews: PreviewProvider {
  static var previews: some View {
    CoinGameView()
  }
}
```

