import StoreKit

class CoinStore {
    let store = SKPaymentQueue.default()
    var coins: Int = 0

    func buyCoins() {
        let product = SKProduct(productID: "com.example.yourappname.coins")
        store.add(product: product) { [weak self] (transaction) in
            self?.coins += 100
        }
    }

    func gamble() {
        let random = Int.random(in: 0...100)
        if random < 50 {
            coins -= 10
        } else {
            coins += 20
        }
    }
}
