```cpp
#include <iostream>
#include <fstream>
#include <stdexcept>

// Function to read binary data from a file in chunks
void readBinaryData(const std::string& filename, int chunkSize) {
    std::ifstream file(filename, std::ios::binary);
    if (!file) {
        std::cerr << "Error opening file: " << filename << std::endl;
        return;
    }

    char buffer[chunkSize];
    while (file.read(buffer, chunkSize)) {
        // Display the chunk of data on the console
        std::cout.write(buffer, chunkSize);
        std::cout << std::endl;
    }

    // Check if there was an error reading the file
    if (file.fail()) {
        std::cerr << "Error reading file: " << filename << std::endl;
    }

    file.close();
}

int main() {
    // Specify the filename and chunk size
    std::string filename = "example.bin";
    int chunkSize = 16; // Adjust this value to change the chunk size

    try {
        readBinaryData(filename, chunkSize);
    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}
