Here is a Pascal program that implements the bubble sort algorithm to sort a list of records containing student names and their corresponding grades:
```
program BubbleSortStudentRecords;

type
  StudentRecord = record
    Name: string;
    Grade: integer;
  end;

var
  numRecords: integer;
  studentRecords: array[1..100] of StudentRecord;
  i, j: integer;
  sorted: boolean;
  order: char;

procedure BubbleSort(var arr: array of StudentRecord; order: char);
var
  i, j: integer;
  temp: StudentRecord;
begin
  sorted := false;
  while not sorted do
  begin
    sorted := true;
    for i := 1 to Length(arr) - 1 do
    begin
      if (order = 'A' and arr[i].Grade > arr[i + 1].Grade) or
         (order = 'D' and arr[i].Grade < arr[i + 1].Grade) then
      begin
        temp := arr[i];
        arr[i] := arr[i + 1];
        arr[i + 1] := temp;
        sorted := false;
      end;
    end;
  end;
end;

begin
  writeln('Enter the number of student records to sort:');
  readln(numRecords);
  if numRecords < 1 or numRecords > 100 then
  begin
    writeln('Invalid input. Please enter a number between 1 and 100.');
    halt;
  end;

  writeln('Enter the student records:');
  for i := 1 to numRecords do
  begin
    readln(studentRecords[i].Name);
    readln(studentRecords[i].Grade);
  end;

  writeln('Choose the sorting order (A for ascending, D for descending):');
  readln(order);
  if order <> 'A' and order <> 'D' then
  begin
    writeln('Invalid input. Please enter A or D.');
    halt;
  end;

  BubbleSort(studentRecords, order);

  writeln('Sorted student records:');
  for i := 1 to numRecords do
  begin
    writeln(studentRecords[i].Name, ': ', studentRecords[i].Grade);
  end;
end.
