Here's a sample C++ program that reads binary data from a file and displays it on the console:

```cpp
#include <iostream>
#include <fstream>
#include <cstdlib>
#include <cstdint>

constexpr size_t kChunkSize = 1024;

void print_binary(const uint8_t* data, size_t size) {
  for (size_t i = 0; i < size; ++i) {
    if (i != 0 && i % 16 == 0) {
      std::cout << " ";
    }
    std::cout << std::hex << std::setw(2) << static_cast<int>(data[i]) << " ";
    if (i % 16 == 15) {
      std::cout << "\n";
    }
  }
}

void read_binary_file(const std::string& filename) {
  std::ifstream file(filename, std::ios::binary);
  if (!file) {
    std::cerr << "Error: Unable to open file: " << filename << "\n";
    return;
  }

  uint8_t buffer[kChunkSize];
  size_t bytes_read = 0;
  while (file.read(reinterpret_cast<char*>(buffer), kChunkSize) == kChunkSize) {
    print_binary(buffer, kChunkSize);
    bytes_read += kChunkSize;
  }

  if (file.gcount() > 0) {
    print_binary(buffer, file.gcount());
  }
}

int main(int argc, char* argv[]) {
  if (argc != 2) {
    std::cerr << "Usage: " << argv[0] << " <filename>\n";
    return 1;
  }

  read_binary_file(argv[1]);
  return 0;
}
