#include <iostream>
#include <fstream>
#include <string>
#include <stdexcept>

// Function to read binary data from a file in chunks
std::string readBinaryData(const std::string& filename, int chunkSize) {
    std::ifstream file(filename, std::ios::binary);
    if (!file) {
        throw std::runtime_error("Error opening file: " + filename);
    }

    std::string data;
    char buffer[chunkSize];

    while (file.read(buffer, chunkSize)) {
        data += buffer;
    }

    if (file.gcount() > 0) {
        data += file.rdbuf()->in_avail();
    }

    file.close();

    return data;
}

// Function to display binary data in a human-readable format
void displayData(const std::string& data) {
    for (char c : data) {
        std::cout << std::hex << static_cast<int>(c) << " ";
    }
    std::cout << std::endl;
}

