        int chunkSize = 1024;

        // Read the binary data in chunks
        std::string data = readBinaryData(filename, chunkSize);

        // Display the binary data in a human-readable format
        displayData(data);

    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}
