#include <iostream>
#include <fstream>
#include <vector>
#include <stdexcept>

// Function to read a binary file and display its contents
void readAndDisplayBinaryFile(const std::string& filename) {
    // Open the file in binary mode
    std::ifstream file(filename, std::ios::binary | std::ios::ate);
    if (!file.is_open()) {
        throw std::runtime_error("Failed to open file: " + filename);
    }

    // Get the file size
    std::streampos fileSize = file.tellg();
    file.seekg(0, std::ios::beg);

    // Read the file in chunks
    const size_t chunkSize = 1024; // Define the chunk size
    std::vector<char> buffer(chunkSize);

    while (file.read(buffer.data(), chunkSize)) {
        // Process the chunk
        processChunk(buffer.data(), chunkSize);
    }

    // Handle the last chunk if it's smaller than the chunk size
    if (file.gcount() > 0) {
        processChunk(buffer.data(), file.gcount());
    }

    // Close the file
    file.close();
}

// Function to process a chunk of data
void processChunk(const char* data, size_t size) {
    for (size_t i = 0; i < size; ++i) {
        // Convert the binary data to a human-readable format
        int value = static_cast<int>(data[i]);
        std::cout << "0x" << std::hex << std::setw(2) << std::setfill('0') << value << " ";
    }
    std::cout << std::dec << std::endl; // Reset to decimal for further output
}

int main() {
    try {
        // Specify the binary file to read
        std::string filename = "data.bin";
        readAndDisplayBinaryFile(filename);
    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}
