#include <iostream>
#include <fstream>
#include <string>
#include <sstream>

using namespace std;

// Function to read a chunk of data from the binary file
void readChunk(ifstream &file, int chunkSize, string &chunkData) {
    char buffer[chunkSize];
    file.read(buffer, chunkSize);
    chunkData = string(buffer, chunkSize);
}

// Function to convert a binary chunk to a human-readable format
string convertToReadable(string chunkData) {
    stringstream ss;
    for (int i = 0; i < chunkData.size(); i++) {
        ss << hex << (int)chunkData[i];
    }
    return ss.str();
}

int main() {
    ifstream file("binaryFile.bin", ios::binary);
    if (!file) {
        cout << "Error: Unable to open file." << endl;
        return 1;
    }

    string chunkData;
    int chunkSize = 1024; // Size of each chunk to read

    while (true) {
        readChunk(file, chunkSize, chunkData);
        if (file.eof()) {
            break;
        }
        if (file.fail()) {
            cout << "Error: Failed to read data." << endl;
            return 1;
        }
        cout << convertToReadable(chunkData) << endl;
    }

    file.close();
    return 0;
}

program BubbleSort;

type
  TStudent = record
    Name: string;
    Grade: integer;
  end;

var
  i, j, n, choice: integer;
  temp: TStudent;
  students: array of TStudent;

begin
  writeln('Enter the number of students: ');
  readln(n);

  setlength(students, n);

  for i := 0 to n - 1 do
  begin
    writeln('Enter the name and grade of student ', i + 1, ': ');
    readln(students[i].Name, students[i].Grade);
  end;

  writeln('Choose the sorting order: ');
  writeln('1. Ascending');
  writeln('2. Descending');
  readln(choice);

  if choice = 1 then
  begin
    for i := 0 to n - 2 do
      for j := 0 to n - i - 2 do
        if students[j].Grade > students[j + 1].Grade then
        begin
          temp := students[j];
          students[j] := students[j + 1];
          students[j + 1] := temp;
        end;
  end
  else if choice = 2 then
  begin
    for i := 0 to n - 2 do
      for j := 0 to n - i - 2 do
        if students[j].Grade < students[j + 1].Grade then
        begin
          temp := students[j];
          students[j] := students[j + 1];
          students[j + 1] := temp;
        end;
  end
  else
  begin
    writeln('Invalid choice. Sorting in ascending order.');
    for i := 0 to n - 2 do
      for j := 0 to n - i - 2 do
        if students[j].Grade > students[j + 1].Grade then
        begin
          temp := students[j];
          students[j] := students[j + 1];
          students[j + 1] := temp;
        end;
  end;

  writeln('Sorted list of students: ');
  for i := 0 to n - 1 do
    writeln(students[i].Name, ' ', students[i].Grade);
end.

def divisible_by_3_and_5(num):
    if not isinstance(num, int):
        return False
    return num % 3 == 0 and num % 5 == 0
