  file.read(data.data(), data.size());
  if (file.eof())
  {
    file.close();
    cerr << "Error reading data from file" << endl;
  }
}

int main()
{
  ifstream file("data.bin");
  vector<int> data;
  read_data(file, data);
  for (int i = 0; i < data.size(); i++)
  {
    cout << "Data: " << data[i] << endl;
  }
  file.close();
  return 0;
}
```

