func isPrime(n *big.Int) bool {
    if n.Cmp(big.NewInt(2)) <= 0 {
        return n.Cmp(big.NewInt(1)) == 0
    }

    root := new(big.Int).Sqrt(n)
    root = root.Rsh(root, 1)
    for i := new(big.Int).Set(root).Sub(root, big.NewInt(1)).SetBit(0, 1); i.Cmp(big.One) > 0; i = i.Steps(2, -2) {
        if n.Mod(n, i).Cmp(big.One) == 0 {
            return false
        }
    }
    return true
}

func main() {
    const min = 1001
    const max = 1e6

    primes := make([]*big.Int, 3)
    for i := range primes {
        num := new(big.Int).Lsh(big.NewInt(2), rand.Int63n(max-min)+min)
        for !isPrime(num) {
            num.Add(num, big.NewInt(2))
        }
        primes[i] = num
    }

    product := new(big.Int)
    for _, prime := range primes {
        product.Mul(product, prime)
    }

