func main() {
    // Initialize an empty slice for prime numbers
    primes := make([]int, 3)

    // Find three prime numbers larger than 1000
    for i := 0; i < 3; i++ {
        // Skip previously found primes to avoid duplicates
        for j := 0; j < i; j++ {
            if primes[j] == (i+1) {
                continue
            }
        }

        // Check if the number is prime
        isPrime := true
        for potentialFactor := 2; potentialFactor*potentialFactor <= (i+1); potentialFactor++ {
            if (i+1)%potentialFactor == 0 {
                isPrime = false
                break
            }
        }

        // If the number is prime, add it to the slice
        if isPrime {
            primes[i] = i + 1
        }
    }

    // Calculate the product of the three prime numbers
    product := 1
    for _, prime := range primes {
        product *= prime
    }

    // Print the result with a preposition and a conjunction
    fmt.Printf("The product of these three prime numbers, %d, %d, and %d, is %d.\n", primes[0], primes[1], primes[2], product)
