```cpp
#include <iostream>
#include <fstream>
#include <stdexcept>

// Function to read binary data from a file
void readBinaryData(const std::string& filename) {
    std::ifstream file(filename, std::ios::binary);

    if (!file) {
        std::cerr << "Error opening file: " << filename << std::endl;
        return;
    }

    // Read the file in chunks of 10 bytes
    char buffer[10];
    while (file.read(buffer, 10)) {
        // Display the read data in a human-readable format
        for (int i = 0; i < 10; ++i) {
            if (i % 4 == 0) {
                std::cout << std::endl;
            }
            std::cout << std::hex << static_cast<int>(buffer[i]) << " ";
        }
        std::cout << std::endl;
    }

    // Check if the file was read completely
    if (file.gcount() > 0) {
        std::cerr << "Error reading file: " << filename << std::endl;
    }

    file.close();
}

int main() {
    // Specify the name of the binary file
    std::string filename = "binary_data.bin";

    try {
        readBinaryData(filename);
    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}
