// Function to read a chunk of data from the binary file
void readChunk(const std::string& filename, size_t chunkSize) {
    std::ifstream file(filename, std::ios::binary | std::ios::ate);
    if (!file.is_open()) {
        throw std::runtime_error("Failed to open file: " + filename);
    }

    // Get the file size
    std::streampos fileSize = file.tellg();
    file.seekg(0, std::ios::beg);

    // Read the file in chunks
    std::vector<char> buffer(chunkSize);
    size_t bytesRead = 0;
    while (file.read(buffer.data(), chunkSize)) {
        bytesRead += file.gcount();
        displayChunk(buffer.data(), bytesRead);
    }

    // Check if we read the entire file
    if (bytesRead != fileSize) {
        throw std::runtime_error("Read beyond the end of the file");
    }

    file.close();
}

// Function to display the chunk of data in a human-readable format
void displayChunk(const char* data, size_t size) {
    for (size_t i = 0; i < size; ++i) {
        std::cout << std::hex << (int)data[i] << " ";
    }
    std::cout << std::dec << std::endl;
}

int main() {
    try {
        const std::string filename = "data.bin";
        const size_t chunkSize = 1024; // Read 1024 bytes at a time

        readChunk(filename, chunkSize);
    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}
