#include <iostream>
#include <fstream>
#include <vector>
#include <stdexcept>

// Function to read a binary file and display its contents
void readAndDisplayBinaryFile(const std::string& filename) {
    std::ifstream file(filename, std::ios::binary);
    if (!file.is_open()) {
        throw std::runtime_error("Failed to open file: " + filename);
    }

    // Determine the file size
    file.seekg(0, std::ios::end);
    std::streampos fileSize = file.tellg();
    file.seekg(0, std::ios::beg);

    // Read the file in chunks
    const size_t chunkSize = 1024; // Define the chunk size
    std::vector<char> buffer(chunkSize);

    while (file.read(buffer.data(), chunkSize)) {
        displayChunk(buffer);
    }

    // Handle the last chunk if it's smaller than the chunk size
    if (file.gcount() > 0) {
        displayChunk(buffer);
    }

    file.close();
}

// Function to display a chunk of data in a human-readable format
void displayChunk(const std::vector<char>& chunk) {
    for (size_t i = 0; i < chunk.size(); ++i) {
        if (i > 0 && i % 16 == 0) {
            std::cout << std::endl;
        }
        std::cout << std::hex << std::setw(2) << std::setfill('0') << static_cast<int>(chunk[i]) << " ";
    }
    std::cout << std::endl;
}

int main() {
    try {
        std::string filename;
        std::cout << "Enter the name of the binary file: ";
        std::cin >> filename;

        readAndDisplayBinaryFile(filename);
    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}
