<?php

// Define the file upload function
function uploadImage() {

  // Get the uploaded file information
  $fileTmpName = $_FILES['image']['name'];
  $fileTmpSize = $_FILES['image']['size'];
  $fileTmpType = $_FILES['image']['type'];

  // Validates the file extension
  if (!in_array(array('jpeg','jpg','png','gif'), strtolower($fileTmpType)) {
    return 'Invalid file extension';
  }

  // Allowed file size
  if ($fileTmpSize > 2097152) {
    return 'File size too large';
  }

  // Upload the file to server
  move_uploaded_file($fileTmpName, $fileTmpSize, $target_directory);

  // Return the uploaded image file path
  return 'uploads/'. $fileTmpName;
}

?>
