#include <iostream>
#include <fstream>
#include <iomanip>

using namespace std;

int main() {
    ifstream file("binaryfile.bin", ios::binary);
    if (!file) {
        cout << "Error opening file" << endl;
        return 1;
    }

    int num;
    while (file.read(reinterpret_cast<char*>(&num), sizeof(num))) {
        cout << "Number: " << num << endl;
    }

    if (file.eof()) {
        cout << "End of file reached" << endl;
    } else if (file.fail()) {
        cout << "Error reading file" << endl;
    }

    file.close();
    return 0;
}

