program BubbleSort;

type
  student = record
    name: string;
    grade: integer;
  end;

  studentArray = array of student;

var
  students: studentArray;
  n, i, j: integer;
  temp: student;
  ascending: boolean;

procedure bubbleSort(var arr: studentArray; ascending: boolean);
var
  swapped: boolean;
  i, j: integer;
  temp: student;
begin
  swapped := true;
  while swapped do
  begin
    swapped := false;
    for i := 0 to Length(arr) - 2 do
    begin
      if (ascending and (arr[i].grade > arr[i + 1].grade)) or
         (not ascending and (arr[i].grade < arr[i + 1].grade)) then
      begin
        temp := arr[i];
        arr[i] := arr[i + 1];
        arr[i + 1] := temp;
        swapped := true;
      end;
    end;
  end;
end;

begin
