    writeln('Enter the name and grade of student ', i + 1, ': ');
    readln(students[i].name, students[i].grade);
  end;
  writeln('Enter 1 to sort in ascending order or 0 to sort in descending order: ');
  readln(ascending);
  bubbleSort(students, ascending);
