#include <fstream>
#include <string>
using namespace std;

void main()
{
  string filename;
  ifstream file;
  string data;
  string line;
  cout << "Enter the filename of the binary file: ";
  cin >> filename;
  file.open(filename);
  if(!file.is_open())
  {
    cout << "Error opening file: " << filename << endl;
    return;
  }
  cout << "Enter the number of lines to read: ";
  cin >> line;
  for(int i = 0; i < line; i++)
  {
    getline(file, data);
    cout << "Line " << i + 1 << ": " << data << endl;
  }
  file.close();
  return;
}
```
