// Set the drawing event listeners
canvas.addEventListener("mousedown", function(event) {
  drawing = true;
  x1 = event.clientX;
  y1 = event.clientY;
});

canvas.addEventListener("mousemove", function(event) {
  if (drawing) {
    x2 = event.clientX;
    y2 = event.clientY;
    ctx.beginPath();
    ctx.moveTo(x1, y1);
    ctx.lineTo(x2, y2);
    ctx.strokeStyle = color;
    ctx.lineWidth = width;
    ctx.stroke();
