package main
import (
    "fmt"
)

func main() {
    var primes []int
    for i := 1001; ; i++ {
        isPrime := true
        for j := 2; j*j <= i; j++ {
            if i%j == 0 {
                isPrime = false
                break
            }
        }
        if isPrime {
            primes = append(primes, i)
            if len(primes) == 3 {
                break
            }
        }
    }
    product := 1
    for _, prime := range primes {
        product *= prime
    }
