Here is a Pascal program that implements the bubble sort algorithm to sort a list of records containing student names and their corresponding grades:
```pascal
program BubbleSort;
uses
  crt;

type
  StudentRecord = record
    Name: string;
    Grade: integer;
  end;

var
  i, j, n: integer;
  students: array[1..100] of StudentRecord;
  choice: char;

procedure BubbleSort(var arr: array of StudentRecord; ascending: boolean);
var
  i, j: integer;
  temp: StudentRecord;
begin
  for i := 1 to Length(arr) - 1 do
  begin
    for j := 1 to Length(arr) - i do
    begin
      if (ascending and arr[j].Grade > arr[j + 1].Grade) or
         (not ascending and arr[j].Grade < arr[j + 1].Grade) then
      begin
        temp := arr[j];
        arr[j] := arr[j + 1];
        arr[j + 1] := temp;
      end;
    end;
  end;
end;

procedure DisplayStudents(arr: array of StudentRecord);
var
  i: integer;
begin
  for i := 1 to Length(arr) do
  begin
    Write(arr[i].Name,'- ', arr[i].Grade, #13#10);
  end;
end;

begin
  Write('Enter the number of students: ');
  Readln(n);
  if n < 1 or n > 100 then
  begin
    Write('Invalid input. Please enter a number between 1 and 100.');
    Readln;
    Halt;
  end;

  for i := 1 to n do
  begin
    Write('Enter student name: ');
    Readln(students[i].Name);
    Write('Enter student grade: ');
    Readln(students[i].Grade);
  end;

  Write('Do you want to sort in ascending (A) or descending (D) order? ');
  Readln(choice);
  case choice of
    'A', 'a':
      BubbleSort(students, true);
    'D', 'd':
      BubbleSort(students, false);
