const canvas = document.getElementById('canvas');
const ctx = canvas.getContext('2d');

let isDrawing = false;
let x = 0;
let y = 0;

canvas.addEventListener('mousedown', (e) => {
  isDrawing = true;
  x = e.clientX;
  y = e.clientY;
});

canvas.addEventListener('mousemove', (e) => {
  if (isDrawing) {
    let dx = e.clientX - x;
    let dy = e.clientY - y;
    x = e.clientX;
    y = e.clientY;

    ctx.beginPath();
    ctx.moveTo(x - dx, y - dy);
    ctx.lineTo(x, y);
    ctx.stroke();
  }
});

canvas.addEventListener('mouseup', () => {
  isDrawing = false;
});
