// Generate primes up to limit
func sieveOfEratosthenes(limit int) []bool {
    primes := make([]bool, limit+1)
    for i := 2; i <= limit; i++ {
        primes[i] = true
    }
    for i := 2; i * i <= limit; i++ {
        if primes[i] {
            for j := i * i; j <= limit; j += i {
                primes[j] = false
            }
        }
    }
    return primes
}

func main() {
    var primes []int
    limit := 1000
    sieve := sieveOfEratosthenes(limit)
    var wg sync.WaitGroup
    var mutex sync.Mutex

    for i := 2; i <= limit; i++ {
        if sieve[i] {
            mutex.Lock()
            primes = append(primes, i)
            mutex.Unlock()
            wg.Add(1)
            go func(i int) {
                wg.Done()
            }(i)
        }
    }
    wg.Wait()

    var product int
    for _, prime := range primes {
        if prime > 1000 {
            mutex.Lock()
            if product == 0 {
                product = prime
            } else {
                product *= prime
            }
            mutex.Unlock()
            if len(primes)-1 == 3 {
                break
            }
        }
    }
    fmt.Printf("The product of %d, %s, and %d is %d.\n", primes[0], primes[primes[0] == primes[1]], primes[2], product)
}
