constexpr size_t CHUNK_SIZE = 16;

void print_binary(const uint8_t* data, size_t size) {
    for (size_t i = 0; i < size; ++i) {
        std::cout << std::setw(2) << std::hex << static_cast<int>(data[i]) << " ";
        if ((i + 1) % 4 == 0) {
            std::cout << " ";
        }
    }
    std::cout << std::endl;
}

