func isPrime(n int) bool {
    if n <= 1 {
        return false
    }
    for i := 2; i <= int(math.Sqrt(float64(n))); i++ {
        if n%i == 0 {
            return false
        }
    }
    return true
}

func main() {
    var primes []int
    for i := 1000; i <= 2000; i++ {
        if isPrime(i) {
            primes = append(primes, i)
        }
