        fmt.Println("There are not enough prime numbers.")
        return
    }
    product := 1
    for _, prime := range primes[:3] {
        product *= prime
    }
    fmt.Printf("The product of %d, %d, and %d is %d, which is the largest product of three prime numbers between 1000 and 2000.\n", primes[0], primes[1], primes[2], product)
