#include <iostream>
#include <fstream>
#include <vector>
#include <stdexcept>

// Function to read a binary file and display its contents
void readAndDisplayBinaryFile(const std::string& filename) {
    std::ifstream file(filename, std::ios::binary | std::ios::ate);
    if (!file.is_open()) {
        throw std::runtime_error("Failed to open file: " + filename);
    }

    // Get the file size
    std::streampos fileSize = file.tellg();
    file.seekg(0, std::ios::beg);

    // Read the file in chunks
    std::vector<char> buffer(fileSize);
    file.read(buffer.data(), fileSize);

    if (file.fail()) {
        throw std::runtime_error("Failed to read file: " + filename);
    }

    // Display the contents in a human-readable format
    for (size_t i = 0; i < buffer.size(); ++i) {
        if (i % 16 == 0) {
            std::cout << std::endl;
        }
        std::cout << std::hex << std::setw(2) << std::setfill('0') << static_cast<int>(buffer[i]) << " ";
    }
    std::cout << std::endl;
}

int main() {
    try {
        std::string filename;
        std::cout << "Enter the name of the binary file: ";
        std::cin >> filename;

        readAndDisplayBinaryFile(filename);
    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}
```

