from fastapi import FastAPI
from pydantic import Model, fields

class GroupDTO(Model):
    id = fields.IntField()
    name = fields.StringField()
    thumbnail_url = fields.StringField()
    creation_date = fields.DateTimeField()

class ReadGroupDTO(GroupDTO):
    pass

class WriteGroupDTO(GroupDTO):
    name = fields.StringField()
    thumbnail_url = fields.StringField()

app = FastAPI()

@app.route("/groups", methods=["GET"]
def get_all_groups():
    return GroupDTO.query.all()

@app.route("/groups", methods=["POST"]
def create_group():
    data = request.json()
    group = WriteGroupDTO(**data)
    group.save()
    return jsonify(group)

@app.route("/groups/<int:group_id>", methods=["GET"]
def get_group(group_id):
    group = GroupDTO.query.get(id=group_id)
    if group:
        return jsonify(group)
    else:
        return jsonify({"error": "Group not found"})

@app.route("/groups/<int:group_id>", methods=["PUT"]
def update_group(group_id):
    group = GroupDTO.query.get(id=group_id)
    if group:
        data = request.json()
        group.name = data["name"]
        group.thumbnail_url = data["thumbnail_url"]
        group.save()
        return jsonify(group)
    else:
        return jsonify({"error": "Group not found"})

@app.route("/groups/<int:group_id>", methods=["DELETE"]
def delete_group(group_id):
    GroupDTO.query.where(id=group_id).delete()
    return jsonify({"message": "Group deleted"})

if __name__ == "__main__":
    app.run()
