```go
package main
import (
    "fmt"
    "math"
)
func main() {
    var primes []int
    for i := 1001; i <= 10000; i++ {
        if isPrime(i) {
            primes = append(primes, i)
        }
    }
    if len(primes) >= 3 {
        product := 1
        for _, prime := range primes[:3] {
            product *= prime
        }
        fmt.Printf("The product of the first three prime numbers larger than 1000 is %d.\n", product)
    } else {
        fmt.Println("There are less than three prime numbers larger than 1000.")
    }
}
func isPrime(n int) bool {
    if n <= 1 {
        return false
    }
    for i := 2; i <= int(math.Sqrt(float64(n))); i++ {
        if n%i == 0 {
            return false
        }
    }
    return true
}
```
